contracts/bluecheck/BlueCheckRegistry.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title BlueCheckRegistry
 * @notice Handles Blue Check purchases with automatic fee splitting
 * @dev $10 USD payment with 70% to Treasury, 30% to Referral Pool
 */
contract BlueCheckRegistry is Ownable, ReentrancyGuard, Pausable {
    // ===================== Constants =====================

    /// @notice Price in USD (18 decimals, like wei)
    uint256 public constant PRICE_USD = 10 * 1e18; // $10

    /// @notice Fee distribution percentages
    uint256 public constant TREASURY_SHARE = 70; // 70%
    uint256 public constant REFERRAL_POOL_SHARE = 30; // 30%

    // ===================== State Variables =====================

    /// @notice Treasury address (receives 70%)
    address public treasury;

    /// @notice Referral pool address (receives 30%)
    address public referralPool;

    /// @notice Price oracle address (for BNB/USD conversion)
    address public priceOracle;

    /// @notice Manual price in BNB (18 decimals) - fallback if oracle fails
    uint256 public manualPriceBNB;

    /// @notice Track who has purchased Blue Check
    mapping(address => bool) public hasBlueCheck;

    /// @notice Track purchase timestamps
    mapping(address => uint256) public purchaseTimestamp;

    /// @notice Total purchases counter
    uint256 public totalPurchases;

    /// @notice Total BNB collected
    uint256 public totalBNBCollected;

    // ===================== Events =====================

    /**
     * @notice Emitted when Blue Check is purchased
     * @param user Address of the purchaser
     * @param amountPaid BNB amount paid
     * @param treasuryAmount BNB sent to treasury
     * @param referralPoolAmount BNB sent to referral pool
     * @param timestamp Purchase timestamp
     */
    event BlueCheckPurchased(
        address indexed user,
        uint256 amountPaid,
        uint256 treasuryAmount,
        uint256 referralPoolAmount,
        uint256 timestamp
    );

    /**
     * @notice Emitted when treasury address is updated
     * @param oldTreasury Previous treasury address
     * @param newTreasury New treasury address
     */
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);

    /**
     * @notice Emitted when referral pool address is updated
     * @param oldPool Previous pool address
     * @param newPool New pool address
     */
    event ReferralPoolUpdated(address indexed oldPool, address indexed newPool);

    /**
     * @notice Emitted when manual price is updated
     * @param oldPrice Previous price in BNB
     * @param newPrice New price in BNB
     */
    event ManualPriceUpdated(uint256 oldPrice, uint256 newPrice);

    // ===================== Errors =====================

    error AlreadyPurchased();
    error InsufficientPayment();
    error InvalidAddress();
    error TransferFailed();

    // ===================== Constructor =====================

    /**
     * @notice Initialize the contract
     * @param _treasury Treasury address
     * @param _referralPool Referral pool address
     * @param _initialPriceBNB Initial BNB price (fallback)
     */
    constructor(
        address _treasury,
        address _referralPool,
        uint256 _initialPriceBNB
    ) Ownable(msg.sender) {
        if (_treasury == address(0) || _referralPool == address(0)) {
            revert InvalidAddress();
        }

        treasury = _treasury;
        referralPool = _referralPool;
        manualPriceBNB = _initialPriceBNB;
    }

    // ===================== Main Functions =====================

    /**
     * @notice Purchase Blue Check (lifetime access)
     * @dev Sends 70% to treasury, 30% to referral pool
     */
    function purchaseBlueCheck() external payable nonReentrant whenNotPaused {
        if (hasBlueCheck[msg.sender]) {
            revert AlreadyPurchased();
        }

        // Get required BNB amount
        uint256 requiredBNB = getRequiredBNB();

        if (msg.value < requiredBNB) {
            revert InsufficientPayment();
        }

        // Mark as purchased
        hasBlueCheck[msg.sender] = true;
        purchaseTimestamp[msg.sender] = block.timestamp;
        totalPurchases++;
        totalBNBCollected += requiredBNB;

        // Calculate splits
        uint256 treasuryAmount = (requiredBNB * TREASURY_SHARE) / 100;
        uint256 referralAmount = requiredBNB - treasuryAmount; // Ensures no rounding issues

        // Transfer to treasury
        (bool treasurySuccess, ) = treasury.call{value: treasuryAmount}("");
        if (!treasurySuccess) {
            revert TransferFailed();
        }

        // Transfer to referral pool
        (bool poolSuccess, ) = referralPool.call{value: referralAmount}("");
        if (!poolSuccess) {
            revert TransferFailed();
        }

        // Emit event for indexer
        emit BlueCheckPurchased(
            msg.sender,
            requiredBNB,
            treasuryAmount,
            referralAmount,
            block.timestamp
        );

        // Refund excess if any
        uint256 excess = msg.value - requiredBNB;
        if (excess > 0) {
            (bool refundSuccess, ) = msg.sender.call{value: excess}("");
            if (!refundSuccess) {
                revert TransferFailed();
            }
        }
    }

    /**
     * @notice Get required BNB for $10 USD
     * @dev Uses manual price (oracle integration can be added later)
     * @return Required BNB amount in wei
     */
    function getRequiredBNB() public view returns (uint256) {
        // TODO: Integrate with Chainlink or price oracle
        // For now, use manual price

        if (manualPriceBNB == 0) {
            revert("Price not set");
        }

        // manualPriceBNB is price of 1 BNB in USD (18 decimals)
        // PRICE_USD is $10 in 18 decimals
        // Required BNB = PRICE_USD / manualPriceBNB

        return (PRICE_USD * 1e18) / manualPriceBNB;
    }

    /**
     * @notice Check if address has Blue Check
     * @param user Address to check
     * @return bool True if user has Blue Check
     */
    function checkBlueCheck(address user) external view returns (bool) {
        return hasBlueCheck[user];
    }

    // ===================== Admin Functions =====================

    /**
     * @notice Update treasury address
     * @param _newTreasury New treasury address
     */
    function updateTreasury(address _newTreasury) external onlyOwner {
        if (_newTreasury == address(0)) {
            revert InvalidAddress();
        }

        address oldTreasury = treasury;
        treasury = _newTreasury;

        emit TreasuryUpdated(oldTreasury, _newTreasury);
    }

    /**
     * @notice Update referral pool address
     * @param _newPool New pool address
     */
    function updateReferralPool(address _newPool) external onlyOwner {
        if (_newPool == address(0)) {
            revert InvalidAddress();
        }

        address oldPool = referralPool;
        referralPool = _newPool;

        emit ReferralPoolUpdated(oldPool, _newPool);
    }

    /**
     * @notice Update manual BNB price
     * @param _newPriceBNB New price (1 BNB in USD, 18 decimals)
     * @dev Example: If 1 BNB = $600, pass 600 * 1e18
     */
    function updateManualPrice(uint256 _newPriceBNB) external onlyOwner {
        uint256 oldPrice = manualPriceBNB;
        manualPriceBNB = _newPriceBNB;

        emit ManualPriceUpdated(oldPrice, _newPriceBNB);
    }

    /**
     * @notice Pause contract (emergency)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Emergency withdraw (only if stuck funds)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        (bool success, ) = owner().call{value: amount}("");
        if (!success) {
            revert TransferFailed();
        }
    }
}

contracts/fairlaunch/Fairlaunch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IFeeSplitter {
    function distributeFeeNative() external payable;
    function distributeFeeERC20(address token, uint256 amount) external;
}

/**
 * @title Fairlaunch
 * @notice Fairlaunch pool with price discovery mechanism - NO hardcap, only softcap
 * @dev Implements price discovery: final_price = totalRaised / tokenForSale
 */
contract Fairlaunch is ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // State Machine
    enum Status {
        PENDING,            // Initial state after deployment
        TOKEN_DEPOSITED,    // Owner deposited sale tokens
        UPCOMING,           // Before start time
        LIVE,               // Active contribution period
        ENDED,              // After end time, awaiting finalization
        FINALIZED_SUCCESS,  // Softcap reached, liquidity added
        FINALIZED_FAIL,     // Softcap not reached
        REFUNDING,          // Processing refunds
        CANCELLED           // Admin cancelled
    }

    struct FeeConfig {
        uint256 totalBps;          // Total fee in basis points (500 = 5%)
        uint256 treasuryBps;       // Treasury share (250 = 2.5%)
        uint256 referralPoolBps;   // Referral pool share (200 = 2%)
        uint256 sbtStakingBps;     // SBT staking share (50 = 0.5%)
    }

    struct PriceDiscovery {
        uint256 finalPrice;         // Calculated at finalization
        uint256 listingPrice;       // finalPrice * (1 + listingPremiumBps/10000)
        uint256 userAllocation;     // Per user: contribution * tokenForSale / totalRaised
    }

    // Immutable Config
    address public immutable saleToken;
    address public immutable paymentToken; // address(0) for native
    uint256 public immutable tokenForSale;
    uint256 public immutable softCap;
    uint256 public immutable minContribution;
    uint256 public immutable maxContribution;
    uint256 public immutable startTime;
    uint256 public immutable endTime;
    uint256 public immutable listingPremiumBps; // e.g., 500 = 5% above final price

    // External Contracts
    address public immutable feeSplitter;
    address public immutable projectOwner;

    // Fee Config
    FeeConfig public feeConfig;

    // State
    Status public status;
    uint256 public totalRaised;
    uint256 public finalPrice;      // Set at finalization
    uint256 public listingPrice;    // Set at finalization
    uint256 public tgeTimestamp;    // Set at finalization

    // Mappings
    mapping(address => uint256) public contributions;
    mapping(address => uint256) public tokenAllocations; // Set at finalization
    mapping(address => bool) public hasClaimed;
    mapping(address => address) public referrers; // contributor => referrer

    // Events
    event TokensDeposited(uint256 amount);
    event Contributed(address indexed contributor, uint256 amount, address indexed referrer);
    event FinalizedSuccess(
        uint256 totalRaised,
        uint256 finalPrice,
        uint256 listingPrice,
        uint256 feeAmount,
        uint256 tgeTimestamp
    );
    event FinalizedFail(string reason);
    event TokensClaimed(address indexed contributor, uint256 amount);
    event Refunded(address indexed contributor, uint256 amount);
    event Cancelled(string reason);
    event StatusSynced(Status oldStatus, Status newStatus);

    // Errors
    error InvalidStatus();
    error SoftCapNotMet();
    error ContributionLimitExceeded();
    error RefundNotAvailable();
    error NoContribution();
    error BelowMinimum();
    error InvalidFeeConfig();
    error ConfigLocked();
    error InvalidAddress();
    error TokensAlreadyDeposited();
    error InsufficientTokenBalance();

    constructor(
        address _saleToken,
        address _paymentToken,
        uint256 _tokenForSale,
        uint256 _softCap,
        uint256 _minContribution,
        uint256 _maxContribution,
        uint256 _startTime,
        uint256 _endTime,
        uint256 _listingPremiumBps,
        address _feeSplitter,
        address _projectOwner,
        address _admin
    ) {
        if (_endTime <= _startTime) revert InvalidStatus();
        if (_feeSplitter == address(0)) revert InvalidAddress();
        if (_projectOwner == address(0)) revert InvalidAddress();
        if (_saleToken == address(0)) revert InvalidAddress();
        if (_tokenForSale == 0) revert InvalidStatus();
        if (_softCap == 0) revert InvalidStatus();

        saleToken = _saleToken;
        paymentToken = _paymentToken;
        tokenForSale = _tokenForSale;
        softCap = _softCap;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
        startTime = _startTime;
        endTime = _endTime;
        listingPremiumBps = _listingPremiumBps;
        feeSplitter = _feeSplitter;
        projectOwner = _projectOwner;

        status = Status.PENDING;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);

        // Default fee config: 5% total (2.5% / 2% / 0.5%)
        feeConfig = FeeConfig({
            totalBps: 500,
            treasuryBps: 250,
            referralPoolBps: 200,
            sbtStakingBps: 50
        });
    }

    /**
     * @notice Project owner deposits tokens for sale
     * @dev Must be called before fairlaunch can start
     */
    function depositTokens() external {
        // Allow anyone (Factory) to deposit, as long as status is PENDING
        // if (msg.sender != projectOwner) revert InvalidAddress();
        if (status != Status.PENDING) revert TokensAlreadyDeposited();

        // Transfer sale tokens to this contract
        IERC20(saleToken).safeTransferFrom(msg.sender, address(this), tokenForSale);

        status = Status.UPCOMING;
        emit TokensDeposited(tokenForSale);
    }

    /**
     * @notice Sync status based on time
     */
    function _syncStatus() internal {
        Status oldStatus = status;

        if (block.timestamp >= startTime && status == Status.UPCOMING) {
            status = Status.LIVE;
        }

        if (status == Status.LIVE && block.timestamp >= endTime) {
            status = Status.ENDED;
        }

        if (oldStatus != status) {
            emit StatusSynced(oldStatus, status);
        }
    }

    /**
     * @notice Contribute to fairlaunch
     * @dev NO hardcap - unlimited contributions accepted
     */
    function contribute(uint256 amount, address referrer) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
    {
        _syncStatus();

        if (status != Status.LIVE) revert InvalidStatus();

        uint256 newTotal = contributions[msg.sender] + amount;

        if (newTotal < minContribution) revert BelowMinimum();
        if (maxContribution > 0 && newTotal > maxContribution) revert ContributionLimitExceeded();

        // Transfer payment (FULL AMOUNT to escrow)
        if (paymentToken == address(0)) {
            if (msg.value != amount) revert InvalidStatus();
        } else {
            IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        // Record contribution
        contributions[msg.sender] += amount;
        totalRaised += amount;

        // Record referrer (immutable per contributor)
        if (referrer != address(0) && referrers[msg.sender] == address(0)) {
            referrers[msg.sender] = referrer;
        }

        emit Contributed(msg.sender, amount, referrers[msg.sender]);
    }

    /**
     * @notice Finalize fairlaunch successfully (soft cap met)
     * @dev Calculates final price and user allocations via price discovery
     */
    function finalizeSuccess() external onlyRole(ADMIN_ROLE) nonReentrant {
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised < softCap) revert SoftCapNotMet();

        // PRICE DISCOVERY CALCULATION
        // final_price = totalRaised / tokenForSale
        finalPrice = (totalRaised * 1e18) / tokenForSale; // Normalize to 18 decimals

        // listing_price = final_price * (1 + listingPremiumBps / 10000)
        listingPrice = (finalPrice * (10000 + listingPremiumBps)) / 10000;

        // Calculate fee
        uint256 feeAmount = (totalRaised * feeConfig.totalBps) / 10000;
        uint256 netAmount = totalRaised - feeAmount;

        // Distribute fee via FeeSplitter
        if (paymentToken == address(0)) {
            IFeeSplitter(feeSplitter).distributeFeeNative{value: feeAmount}();
        } else {
            IERC20(paymentToken).forceApprove(feeSplitter, feeAmount);
            IFeeSplitter(feeSplitter).distributeFeeERC20(paymentToken, feeAmount);
        }

        // Transfer net amount to project owner
        if (paymentToken == address(0)) {
            (bool success, ) = projectOwner.call{value: netAmount}("");
            if (!success) revert InvalidStatus();
        } else {
            IERC20(paymentToken).safeTransfer(projectOwner, netAmount);
        }

        // Set TGE timestamp
        tgeTimestamp = block.timestamp;

        // Update status
        status = Status.FINALIZED_SUCCESS;

        emit FinalizedSuccess(totalRaised, finalPrice, listingPrice, feeAmount, tgeTimestamp);
    }

    /**
     * @notice Calculate user's token allocation
     * @dev user_allocation = userContribution * tokenForSale / totalRaised
     */
    function getUserAllocation(address user) public view returns (uint256) {
        if (status != Status.FINALIZED_SUCCESS) return 0;
        if (totalRaised == 0) return 0;

        return (contributions[user] * tokenForSale) / totalRaised;
    }

    /**
     * @notice Claim tokens after successful finalization
     */
    function claimTokens() external nonReentrant {
        if (status != Status.FINALIZED_SUCCESS) revert InvalidStatus();
        if (hasClaimed[msg.sender]) revert InvalidStatus();
        if (contributions[msg.sender] == 0) revert NoContribution();

        uint256 allocation = getUserAllocation(msg.sender);
        hasClaimed[msg.sender] = true;

        IERC20(saleToken).safeTransfer(msg.sender, allocation);

        emit TokensClaimed(msg.sender, allocation);
    }

    /**
     * @notice Finalize fairlaunch as failed (soft cap not met)
     */
    function finalizeFail(string calldata reason) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised >= softCap) revert InvalidStatus();

        status = Status.FINALIZED_FAIL;
        emit FinalizedFail(reason);
    }

    /**
     * @notice Cancel fairlaunch (admin emergency)
     */
    function cancel(string calldata reason) external onlyRole(ADMIN_ROLE) {
        _syncStatus();

        if (status != Status.UPCOMING && status != Status.LIVE) {
            revert InvalidStatus();
        }

        status = Status.CANCELLED;
        emit Cancelled(reason);
    }

    /**
     * @notice Claim refund
     */
    function claimRefund() external nonReentrant {
        if (status != Status.FINALIZED_FAIL && status != Status.CANCELLED) {
            revert RefundNotAvailable();
        }

        uint256 amount = contributions[msg.sender];
        if (amount == 0) revert NoContribution();

        contributions[msg.sender] = 0;

        // Transfer full contribution back (NO fee deduction)
        if (paymentToken == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            if (!success) revert InvalidStatus();
        } else {
            IERC20(paymentToken).safeTransfer(msg.sender, amount);
        }

        emit Refunded(msg.sender, amount);
    }

    /**
     * @notice Return unsold tokens to project owner (if fail/cancel)
     */
    function returnUnsoldTokens() external onlyRole(ADMIN_ROLE) {
        if (status != Status.FINALIZED_FAIL && status != Status.CANCELLED) {
            revert InvalidStatus();
        }

        uint256 balance = IERC20(saleToken).balanceOf(address(this));
        if (balance > 0) {
            IERC20(saleToken).safeTransfer(projectOwner, balance);
        }
    }

    /**
     * @notice Update fee configuration (before start only)
     */
    function setFeeConfig(FeeConfig calldata newConfig) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        if (status != Status.PENDING && status != Status.UPCOMING) revert ConfigLocked();
        if (totalRaised > 0) revert ConfigLocked();
        if (block.timestamp >= startTime) revert ConfigLocked();

        // Validate total equals sum
        if (newConfig.totalBps != 
            newConfig.treasuryBps + 
            newConfig.referralPoolBps + 
            newConfig.sbtStakingBps
        ) {
            revert InvalidFeeConfig();
        }

        feeConfig = newConfig;
    }

    /**
     * @notice Pause contributions only
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Get fairlaunch info
     */
    function getFairlaunchInfo() 
        external 
        view 
        returns (
            Status _status,
            uint256 _totalRaised,
            uint256 _softCap,
            uint256 _tokenForSale,
            uint256 _finalPrice,
            uint256 _listingPrice,
            uint256 _startTime,
            uint256 _endTime,
            uint256 _tgeTimestamp
        ) 
    {
        return (
            status,
            totalRaised,
            softCap,
            tokenForSale,
            finalPrice,
            listingPrice,
            startTime,
            endTime,
            tgeTimestamp
        );
    }

    /**
     * @notice Get contribution info
     */
    function getContributionInfo(address contributor) 
        external 
        view 
        returns (
            uint256 contribution,
            uint256 allocation,
            address referrer,
            bool claimed
        ) 
    {
        return (
            contributions[contributor],
            getUserAllocation(contributor),
            referrers[contributor],
            hasClaimed[contributor]
        );
    }
}

contracts/fairlaunch/FairlaunchFactory.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Fairlaunch.sol";
import "./TeamVesting.sol";

interface IFeeSplitterExt {
    function grantPresaleRole(address presale) external;
}

/**
 * @title FairlaunchFactory
 * @notice Factory for deploying fairlaunch pools with creation fees and compliance tracking
 * @dev Supports multi-chain deployment (BSC, ETH, Base) with per-network fees
 */
contract FairlaunchFactory is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant FACTORY_ADMIN_ROLE = keccak256("FACTORY_ADMIN_ROLE");

    // Creation fee (in native token)
    uint256 public creationFee;

    // External contract references
    address public immutable feeSplitter;
    address payable public treasuryWallet; // Receives 100% of creation fees
    address public immutable timelockExecutor;

    // Fairlaunch tracking
    uint256 public fairlaunchCount;
    mapping(uint256 => address) public fairlaunches;
    mapping(address => bytes32) public fairlaunchToComplianceHash;
    mapping(address => LPLockPlan) public fairlaunchToLPPlan;
    mapping(address => address) public fairlaunchToVesting; // New: map to vesting contract

    struct LPLockPlan {
        uint256 lockMonths;      // Minimum 12 months
        bytes32 dexId;           // DEX identifier (e.g., keccak256("pancakeswap"))
        uint256 liquidityPercent; // Percentage of raised funds for liquidity (BPS, min 7000 = 70%)
    }

    struct VestingParams {
        uint256[] durations;
        uint256[] amounts;
    }

    struct CreateFairlaunchParams {
        address saleToken;
        address paymentToken;
        uint256 tokenForSale;
        uint256 softCap;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        uint256 listingPremiumBps;
        address projectOwner;
    }

    // Events
    event FairlaunchCreated(
        uint256 indexed fairlaunchId,
        address indexed fairlaunch,
        address vestingAddress,
        bytes32 complianceHash,
        uint256 creationFeePaid
    );
    event LPPlanRecorded(
        address indexed fairlaunch,
        uint256 lockMonths,
        bytes32 dexId,
        uint256 liquidityPercent
    );
    event CreationFeeUpdated(uint256 oldFee, uint256 newFee);
    event TreasuryWalletUpdated(address indexed oldWallet, address indexed newWallet);

    // Errors
    error InvalidSoftCap();
    error InvalidTimeRange();
    error InvalidContributionRange();
    error InsufficientLPLockDuration();
    error InvalidLiquidityPercent();
    error ZeroAddress();
    error InsufficientCreationFee();
    error InvalidTokenAmount();

    constructor(
        address _feeSplitter,
        address payable _treasuryWallet,
        address _timelockExecutor,
        uint256 _initialCreationFee
    ) {
        if (_feeSplitter == address(0)) revert ZeroAddress();
        if (_treasuryWallet == address(0)) revert ZeroAddress();
        if (_timelockExecutor == address(0)) revert ZeroAddress();

        feeSplitter = _feeSplitter;
        treasuryWallet = _treasuryWallet;
        timelockExecutor = _timelockExecutor;
        creationFee = _initialCreationFee;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(FACTORY_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new fairlaunch pool
     * @dev Off-chain gates (Dev KYC, SC Scan, Fee Payment, Admin Approval) are validated by backend
     * @param params Fairlaunch configuration parameters
     * @param lpPlan Liquidity lock plan (min 70%, min 12 months)
     * @param vestingParams Team vesting parameters (optional)
     * @param complianceHash Optional hash of off-chain compliance data
     * @return fairlaunch Address of deployed Fairlaunch
     */
    function createFairlaunch(
        CreateFairlaunchParams calldata params,
        LPLockPlan calldata lpPlan,
        VestingParams calldata vestingParams,
        bytes32 complianceHash
    ) external payable onlyRole(FACTORY_ADMIN_ROLE) returns (address fairlaunch) {
        // Validate creation fee
        if (msg.value < creationFee) revert InsufficientCreationFee();

        // Validate parameters
        _validateFairlaunchParams(params, lpPlan);

        // 1. Calculate Total Transfer Amount
        uint256 totalVesting = 0;
        if (vestingParams.amounts.length > 0) {
            for(uint i=0; i<vestingParams.amounts.length; i++) {
                totalVesting += vestingParams.amounts[i];
            }
        }

        uint256 totalRequired = params.tokenForSale + totalVesting;

        // 2. Transfer Tokens to Factory (Temporary Hold)
        // Assume Project Owner has approved Factory
        IERC20(params.saleToken).safeTransferFrom(params.projectOwner, address(this), totalRequired);

        // 3. Deploy Fairlaunch
        fairlaunch = address(new Fairlaunch(
            params.saleToken,
            params.paymentToken,
            params.tokenForSale,
            params.softCap,
            params.minContribution,
            params.maxContribution,
            params.startTime,
            params.endTime,
            params.listingPremiumBps,
            feeSplitter,
            params.projectOwner,
            timelockExecutor  // Admin role for fairlaunch
        ));

        // 4. Fund Fairlaunch
        // To interact with Fairlaunch's depositTokens, we need to approve it first? 
        // Or Fairlaunch.depositTokens expects msg.sender to have tokens.
        // Option A: Factory calls depositTokens.
        // Option B: Factory transfers to ProjectOwner then Owner calls it (No, that breaks workflow).
        // Let's check Fairlaunch.depositTokens() -> it checks msg.sender == projectOwner.

        // ISSUE: Fairlaunch.depositTokens() expects `projectOwner` to call it.
        // PATCH: Fairlaunch needs to allow Factory to deposit.
        // OR: We transfer tokens to Fairlaunch manually, but Fairlaunch state needs update.

        // Solution: Since we are deploying Fairlaunch, we can modify Fairlaunch to accept initial deposit in constructor?
        // Or simply perform transfer and have Fairlaunch detect balance?
        // Current Fairlaunch: `depositTokens()`: `transferFrom(msg.sender, address(this), amount)`.

        // WORKAROUND FOR EXISTING CONTRACT:
        // We cannot easily modify Fairlaunch.sol without redeploying logic if it was already fixed.
        // But since we are allowed to modify contracts, let's assume we can change Fairlaunch or use a hack.
        // If Fairlaunch is immutable in this task context (I can change it per plan), I should change it.
        // BUT, `Fairlaunch.sol` line 163: `if (msg.sender != projectOwner) revert InvalidAddress();`
        // Factory is NOT projectOwner.

        // Wait per `Fairlaunch.sol`: `_grantRole(ADMIN_ROLE, _admin);`.
        // _admin passed in constructor is `timelockExecutor`.
        // Factory is not admin.

        // CRITICAL FIX: We should modify `Fairlaunch.sol` to allow deposits from Factory or Constructor.
        // However, `Fairlaunch.sol` is designed for Owner deposit pattern.
        // Ideally, we transfer tokens to `projectOwner` back? No, risky.

        // Let's modify `Fairlaunch.sol` to accept deposit in constructor? No, `depositTokens` is separate.
        // Let's modify `Fairlaunch.sol` to allow `ADMIN_ROLE` to deposit?
        // But Factory is not Admin.

        // Modification: We will Approve Fairlaunch to spend Factory tokens, and then call a modified deposit function?
        // OR: We simply transfer tokens to Fairlaunch address, and modify Fairlaunch to `status = UPCOMING` if balance > X.

        // BEST APPROACH: Modify `Fairlaunch.sol` to allow direct deposit from deployer (Factory).
        // Check `Fairlaunch.sol` line 162.

        // For now, I will assume we update `Fairlaunch.sol` next.
        // Let's proceed with Factory logic assuming we add `depositFromFactory` to Fairlaunch.

        // Transfer to Fairlaunch
        IERC20(params.saleToken).forceApprove(fairlaunch, params.tokenForSale);
        Fairlaunch(fairlaunch).depositTokens(); // We need to remove owner check or add factory check

        // 5. Deploy & Fund Vesting
        address vestingAddress = address(0);
        if (totalVesting > 0) {
            TeamVesting vesting = new TeamVesting(
                params.saleToken,
                params.projectOwner,
                params.endTime, // Vesting starts after fairlaunch ends
                vestingParams.durations,
                vestingParams.amounts,
                timelockExecutor
            );
            vestingAddress = address(vesting);

            IERC20(params.saleToken).safeTransfer(vestingAddress, totalVesting);
            fairlaunchToVesting[fairlaunch] = vestingAddress;
        }

        // Grant PRESALE_ROLE on FeeSplitter
        IFeeSplitterExt(feeSplitter).grantPresaleRole(fairlaunch);

        // Record mappings
        fairlaunches[fairlaunchCount] = fairlaunch;
        fairlaunchToComplianceHash[fairlaunch] = complianceHash;
        fairlaunchToLPPlan[fairlaunch] = lpPlan;

        // Transfer fee
        (bool success, ) = treasuryWallet.call{value: msg.value}("");
        require(success, "Treasury transfer failed");

        emit FairlaunchCreated(fairlaunchCount, fairlaunch, vestingAddress, complianceHash, msg.value);
        emit LPPlanRecorded(fairlaunch, lpPlan.lockMonths, lpPlan.dexId, lpPlan.liquidityPercent);

        fairlaunchCount++;

        return fairlaunch;
    }

    function _validateFairlaunchParams(
        CreateFairlaunchParams calldata params,
        LPLockPlan calldata lpPlan
    ) internal view {
        if (params.softCap == 0) revert InvalidSoftCap();
        if (params.startTime >= params.endTime) revert InvalidTimeRange();
        if (params.startTime <= block.timestamp) revert InvalidTimeRange();

        if (params.minContribution > params.maxContribution && params.maxContribution > 0) {
            revert InvalidContributionRange();
        }
        if (params.minContribution == 0) revert InvalidContributionRange();

        if (params.tokenForSale == 0) revert InvalidTokenAmount();

        if (lpPlan.lockMonths < 12) revert InsufficientLPLockDuration();
        if (lpPlan.liquidityPercent < 7000 || lpPlan.liquidityPercent > 10000) {
            revert InvalidLiquidityPercent();
        }

        if (params.saleToken == address(0)) revert ZeroAddress();
        if (params.projectOwner == address(0)) revert ZeroAddress();
    }

    function updateCreationFee(uint256 newFee) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldFee = creationFee;
        creationFee = newFee;
        emit CreationFeeUpdated(oldFee, newFee);
    }

    function updateTreasuryWallet(address payable newTreasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newTreasury == address(0)) revert ZeroAddress();
        address oldWallet = treasuryWallet;
        treasuryWallet = newTreasury;
        emit TreasuryWalletUpdated(oldWallet, newTreasury);
    }

    function getFairlaunchDetails(uint256 fairlaunchId) external view returns (
        address fairlaunch,
        bytes32 complianceHash,
        LPLockPlan memory lpPlan,
        address vestingAddress
    ) {
        fairlaunch = fairlaunches[fairlaunchId];
        complianceHash = fairlaunchToComplianceHash[fairlaunch];
        lpPlan = fairlaunchToLPPlan[fairlaunch];
        vestingAddress = fairlaunchToVesting[fairlaunch];
    }

    function getLPPlan(address fairlaunch) external view returns (LPLockPlan memory) {
        return fairlaunchToLPPlan[fairlaunch];
    }

    receive() external payable {}
}

