// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title FeeSplitter
 * @notice Distributes platform fees to Treasury, Referral Pool, and SBT Staking
 * @dev Mock implementation for Fairlaunch testing
 */
contract FeeSplitter is AccessControl, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant PRESALE_ROLE = keccak256("PRESALE_ROLE");

    address payable public treasury;
    address payable public referralPool;
    address payable public sbtStaking;

    // Fee distribution (basis points, total = 500)
    uint256 public constant TREASURY_BPS = 250;      // 2.5%
    uint256 public constant REFERRAL_POOL_BPS = 200; // 2.0%
    uint256 public constant SBT_STAKING_BPS = 50;    // 0.5%
    uint256 public constant TOTAL_BPS = 500;         // 5.0%

    event FeeDistributed(
        address indexed from,
        uint256 totalAmount,
        uint256 treasuryAmount,
        uint256 referralAmount,
        uint256 sbtAmount
    );

    constructor(
        address payable _treasury,
        address payable _referralPool,
        address payable _sbtStaking,
        address _admin
    ) {
        require(_treasury != address(0), "Treasury zero address");
        require(_referralPool != address(0), "Referral pool zero address");
        require(_sbtStaking != address(0), "SBT staking zero address");

        treasury = _treasury;
        referralPool = _referralPool;
        sbtStaking = _sbtStaking;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
    }

    /**
     * @notice Distribute native token fees (ETH/BNB)
     */
    function distributeFeeNative() external payable nonReentrant onlyRole(PRESALE_ROLE) {
        uint256 totalAmount = msg.value;
        require(totalAmount > 0, "Zero amount");

        uint256 treasuryAmount = (totalAmount * TREASURY_BPS) / TOTAL_BPS;
        uint256 referralAmount = (totalAmount * REFERRAL_POOL_BPS) / TOTAL_BPS;
        uint256 sbtAmount = totalAmount - treasuryAmount - referralAmount;

        (bool success1, ) = treasury.call{value: treasuryAmount}("");
        require(success1, "Treasury transfer failed");

        (bool success2, ) = referralPool.call{value: referralAmount}("");
        require(success2, "Referral pool transfer failed");

        (bool success3, ) = sbtStaking.call{value: sbtAmount}("");
        require(success3, "SBT staking transfer failed");

        emit FeeDistributed(msg.sender, totalAmount, treasuryAmount, referralAmount, sbtAmount);
    }

    /**
     * @notice Distribute ERC20 token fees
     */
    function distributeFeeERC20(address token, uint256 amount) 
        external 
        nonReentrant 
        onlyRole(PRESALE_ROLE) 
    {
        require(amount > 0, "Zero amount");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        uint256 treasuryAmount = (amount * TREASURY_BPS) / TOTAL_BPS;
        uint256 referralAmount = (amount * REFERRAL_POOL_BPS) / TOTAL_BPS;
        uint256 sbtAmount = amount - treasuryAmount - referralAmount;

        IERC20(token).safeTransfer(treasury, treasuryAmount);
        IERC20(token).safeTransfer(referralPool, referralAmount);
        IERC20(token).safeTransfer(sbtStaking, sbtAmount);

        emit FeeDistributed(msg.sender, amount, treasuryAmount, referralAmount, sbtAmount);
    }

    /**
     * @notice Grant PRESALE_ROLE to a presale/fairlaunch contract
     */
    function grantPresaleRole(address presale) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(PRESALE_ROLE, presale);
    }

    /**
     * @notice Update treasury address
     */
    function updateTreasury(address payable _treasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_treasury != address(0), "Treasury zero address");
        treasury = _treasury;
    }

    /**
     * @notice Update referral pool address
     */
    function updateReferralPool(address payable _referralPool) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_referralPool != address(0), "Referral pool zero address");
        referralPool = _referralPool;
    }

    /**
     * @notice Update SBT staking address
     */
    function updateSbtStaking(address payable _sbtStaking) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_sbtStaking != address(0), "SBT staking zero address");
        sbtStaking = _sbtStaking;
    }

    receive() external payable {}
}

contracts/std-presale/FeeSplitter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title FeeSplitter
 * @notice Distributes presale fees to 3 vaults: Treasury, Referral Pool, SBT Staking
 * @dev Supports both native tokens and ERC20
 */
contract FeeSplitter is ReentrancyGuard, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant PRESALE_ROLE = keccak256("PRESALE_ROLE");

    struct FeeConfig {
        uint256 totalBps;          // Total fee in basis points (500 = 5%)
        uint256 treasuryBps;       // Treasury share (250 = 2.5%)
        uint256 referralPoolBps;   // Referral pool share (200 = 2%)
        uint256 sbtStakingBps;     // SBT staking share (50 = 0.5%)
    }

    // Vault addresses
    address public treasuryVault;
    address public referralPoolVault;
    address public sbtStakingVault;

    // Fee configuration
    FeeConfig public feeConfig;

    // Events
    event FeeCollected(address indexed token, uint256 totalAmount);
    event FeeSplit(address indexed vault, address indexed token, uint256 amount, uint256 bps);
    event FeeConfigUpdated(FeeConfig newConfig);
    event VaultUpdated(string vaultType, address newVault);

    // Errors
    error InvalidFeeConfig();
    error InvalidVault();
    error TransferFailed();
    error ZeroAmount();

    constructor(
        address _treasury,
        address _referralPool,
        address _sbtStaking,
        address _admin
    ) {
        if (_treasury == address(0)) revert InvalidVault();
        if (_referralPool == address(0)) revert InvalidVault();
        if (_sbtStaking == address(0)) revert InvalidVault();

        treasuryVault = _treasury;
        referralPoolVault = _referralPool;
        sbtStakingVault = _sbtStaking;

        // Default config: 5% total (2.5% / 2% / 0.5%)
        feeConfig = FeeConfig({
            totalBps: 500,
            treasuryBps: 250,
            referralPoolBps: 200,
            sbtStakingBps: 50
        });

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(PRESALE_ROLE, _admin); // Admin can act as presale temporarily
    }

    /**
     * @notice Distribute native token fees
     */
    function distributeFeeNative() external payable nonReentrant onlyRole(PRESALE_ROLE) {
        uint256 totalAmount = msg.value;
        if (totalAmount == 0) revert ZeroAmount();

        _distributeFee(address(0), totalAmount);
    }

    /**
     * @notice Distribute ERC20 token fees
     * @param token ERC20 token address
     * @param totalAmount Total fee amount
     */
    function distributeFeeERC20(address token, uint256 totalAmount) 
        external 
        nonReentrant 
        onlyRole(PRESALE_ROLE) 
    {
        if (totalAmount == 0) revert ZeroAmount();

        // Transfer tokens from caller (presale contract)
        IERC20(token).safeTransferFrom(msg.sender, address(this), totalAmount);

        _distributeFee(token, totalAmount);
    }

    /**
     * @notice Internal fee distribution logic
     * @dev Splits totalAmount based on relative proportions (share-of-fee model)
     * @param token Token address (address(0) for native)
     * @param totalAmount Total fee amount to distribute
     * 
     * Fee Model: vaultAmount = feeAmount * vaultBps / totalBps
     * Rounding Policy: Any remainder goes to treasury vault
     */
    function _distributeFee(address token, uint256 totalAmount) internal {
        // Calculate splits using relative proportions
        // Example: if totalBps=500, treasuryBps=250, then treasury gets 250/500 = 50% of fee
        uint256 treasuryAmount = (totalAmount * feeConfig.treasuryBps) / feeConfig.totalBps;
        uint256 referralAmount = (totalAmount * feeConfig.referralPoolBps) / feeConfig.totalBps;
        uint256 sbtAmount = (totalAmount * feeConfig.sbtStakingBps) / feeConfig.totalBps;

        // Deterministic rounding: remainder goes to treasury
        uint256 remainder = totalAmount - (treasuryAmount + referralAmount + sbtAmount);
        treasuryAmount += remainder;

        // Distribute to vaults
        if (token == address(0)) {
            // Native token distribution
            _transferNative(treasuryVault, treasuryAmount);
            _transferNative(referralPoolVault, referralAmount);
            _transferNative(sbtStakingVault, sbtAmount);
        } else {
            // ERC20 distribution
            IERC20(token).safeTransfer(treasuryVault, treasuryAmount);
            IERC20(token).safeTransfer(referralPoolVault, referralAmount);
            IERC20(token).safeTransfer(sbtStakingVault, sbtAmount);
        }

        emit FeeCollected(token, totalAmount);
        emit FeeSplit(treasuryVault, token, treasuryAmount, feeConfig.treasuryBps);
        emit FeeSplit(referralPoolVault, token, referralAmount, feeConfig.referralPoolBps);
        emit FeeSplit(sbtStakingVault, token, sbtAmount, feeConfig.sbtStakingBps);
    }

    /**
     * @notice Safe native token transfer
     */
    function _transferNative(address to, uint256 amount) internal {
        (bool success, ) = to.call{value: amount}("");
        if (!success) revert TransferFailed();
    }

    /**
     * @notice Update fee configuration
     * @dev Only admin, requires validation. Uses share-of-fee model.
     * @param newConfig New fee configuration
     */
    function setFeeConfig(FeeConfig calldata newConfig) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        // Validate: totalBps must be positive
        if (newConfig.totalBps == 0) revert InvalidFeeConfig();

        // Validate: sum of vault BPS must equal totalBps (share-of-fee model)
        if (newConfig.totalBps != 
            newConfig.treasuryBps + 
            newConfig.referralPoolBps + 
            newConfig.sbtStakingBps
        ) {
            revert InvalidFeeConfig();
        }

        // Validate: reasonable limits (max 10%)
        if (newConfig.totalBps > 1000) revert InvalidFeeConfig();

        feeConfig = newConfig;
        emit FeeConfigUpdated(newConfig);
    }

    /**
     * @notice Update vault addresses
     */
    function setTreasuryVault(address newVault) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newVault == address(0)) revert InvalidVault();
        treasuryVault = newVault;
        emit VaultUpdated("treasury", newVault);
    }

    function setReferralPoolVault(address newVault) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newVault == address(0)) revert InvalidVault();
        referralPoolVault = newVault;
        emit VaultUpdated("referralPool", newVault);
    }

    function setSbtStakingVault(address newVault) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (newVault == address(0)) revert InvalidVault();
        sbtStakingVault = newVault;
        emit VaultUpdated("sbtStaking", newVault);
    }

    /**
     * @notice Grant presale role to new presale contract
     */
    function grantPresaleRole(address presale) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(PRESALE_ROLE, presale);
    }

    /**
     * @notice Receive function for native token
     */
    receive() external payable {
        // Allow receiving native tokens
    }
}

contracts/std-presale/MerkleVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title MerkleVesting
 * @notice Scalable token vesting using Merkle tree for allocation proofs
 * @dev Prevents cross-round proof replay via salted leaf encoding
 */
contract MerkleVesting is ReentrancyGuard, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // Immutable parameters
    address public immutable token;
    uint256 public immutable tgeUnlockBps;     // Basis points unlocked at TGE (1000 = 10%)
    uint256 public immutable cliffDuration;     // Seconds after TGE
    uint256 public immutable vestingDuration;   // Seconds of linear vesting
    bytes32 public immutable scheduleSalt;       // Unique salt for this schedule (hashed)

    // State
    bytes32 public merkleRoot;
    uint256 public tgeTimestamp;
    uint256 public totalAllocated;

    // Per-user claimed amounts
    mapping(address => uint256) public claimed;

    // Events
    event MerkleRootSet(bytes32 indexed merkleRoot, uint256 tgeTimestamp, uint256 totalAllocated);
    event Claimed(address indexed beneficiary, uint256 amount);

    // Errors
    error RootAlreadySet();
    error RootNotSet();
    error InvalidProof();
    error NothingToClaim();
    error InsufficientVaultBalance();
    error ZeroAllocation();

    constructor(
        address _token,
        uint256 _tgeUnlockBps,
        uint256 _cliffDuration,
        uint256 _vestingDuration,
        bytes32 _scheduleSalt,
        address _admin
    ) {
        token = _token;
        tgeUnlockBps = _tgeUnlockBps;
        cliffDuration = _cliffDuration;
        vestingDuration = _vestingDuration;
        scheduleSalt = _scheduleSalt;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
    }

    /**
     * @notice Set Merkle root (called once after presale finalization)
     * @param _merkleRoot Root of allocation Merkle tree
     * @param _totalAllocated Total tokens allocated (for vault balance check)
     */
    function setMerkleRoot(bytes32 _merkleRoot, uint256 _totalAllocated) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        if (merkleRoot != bytes32(0)) revert RootAlreadySet();
        if (_merkleRoot == bytes32(0)) revert InvalidProof();
        if (_totalAllocated == 0) revert ZeroAllocation();

        // CRITICAL: Ensure vault is funded before enabling claims
        uint256 vaultBalance = IERC20(token).balanceOf(address(this));
        if (vaultBalance < _totalAllocated) revert InsufficientVaultBalance();

        merkleRoot = _merkleRoot;
        tgeTimestamp = block.timestamp;
        totalAllocated = _totalAllocated;

        emit MerkleRootSet(_merkleRoot, tgeTimestamp, _totalAllocated);
    }

    /**
     * @notice Claim vested tokens with Merkle proof
     * @param totalAllocation Total tokens allocated to beneficiary
     * @param proof Merkle proof
     */
    function claim(uint256 totalAllocation, bytes32[] calldata proof) 
        external 
        nonReentrant 
    {
        if (merkleRoot == bytes32(0)) revert RootNotSet();

        // V2.1 PATCH: Salted leaf encoding to prevent cross-round replay
        bytes32 leaf = keccak256(abi.encodePacked(
            address(this),          // Contract address
            block.chainid,          // Chain ID
            scheduleSalt,           // Schedule salt (already bytes32)
            msg.sender,             // Beneficiary
            totalAllocation         // Allocation amount
        ));

        // Verify Merkle proof
        if (!MerkleProof.verify(proof, merkleRoot, leaf)) {
            revert InvalidProof();
        }

        // Calculate claimable
        uint256 claimable = _calculateClaimable(totalAllocation);
        uint256 alreadyClaimed = claimed[msg.sender];

        if (claimable <= alreadyClaimed) revert NothingToClaim();

        uint256 toClaim = claimable - alreadyClaimed;

        // Update claimed
        claimed[msg.sender] = claimable;

        // Transfer tokens
        IERC20(token).safeTransfer(msg.sender, toClaim);

        emit Claimed(msg.sender, toClaim);
    }

    /**
     * @notice Calculate claimable amount
     * @dev TGE unlock + cliff + linear vesting
     */
    function _calculateClaimable(uint256 totalAllocation) 
        internal 
        view 
        returns (uint256) 
    {
        // TGE amount
        uint256 tgeAmount = (totalAllocation * tgeUnlockBps) / 10000;

        // Before cliff: only TGE unlocked
        uint256 cliffEnd = tgeTimestamp + cliffDuration;
        if (block.timestamp < cliffEnd) {
            return tgeAmount;
        }

        // After vesting ends: fully unlocked
        uint256 vestingEnd = cliffEnd + vestingDuration;
        if (block.timestamp >= vestingEnd) {
            return totalAllocation;
        }

        // Linear vesting
        uint256 vestedAmount = totalAllocation - tgeAmount;
        uint256 elapsed = block.timestamp - cliffEnd;
        uint256 vestedSoFar = (vestedAmount * elapsed) / vestingDuration;

        return tgeAmount + vestedSoFar;
    }

    /**
     * @notice View: Get claimable amount for beneficiary
     * @param beneficiary Beneficiary address
     * @param totalAllocation Total allocation for beneficiary
     */
    function getClaimable(address beneficiary, uint256 totalAllocation) 
        external 
        view 
        returns (uint256) 
    {
        if (merkleRoot == bytes32(0)) return 0;

        uint256 claimable = _calculateClaimable(totalAllocation);
        uint256 alreadyClaimed = claimed[beneficiary];

        return claimable > alreadyClaimed ? claimable - alreadyClaimed : 0;
    }

    /**
     * @notice View: Get vesting info
     */
    function getVestingInfo() external view returns (
        uint256 _tgeUnlockBps,
        uint256 _cliffDuration,
        uint256 _vestingDuration,
        uint256 _tgeTimestamp,
        bytes32 _merkleRoot
    ) {
        return (
            tgeUnlockBps,
            cliffDuration,
            vestingDuration,
            tgeTimestamp,
            merkleRoot
        );
    }
}

contracts/std-presale/mocks/ERC20Mock.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title ERC20Mock
 * @notice Mock ERC20 token for testing
 */
contract ERC20Mock is ERC20 {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_
    ) ERC20(name, symbol) {
        _decimals = decimals_;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external {
        _burn(from, amount);
    }
}

contracts/std-presale/PresaleFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./PresaleRound.sol";
import "./MerkleVesting.sol";

// Extended interface for FeeSplitter (includes role management)
interface IFeeSplitterExt {
    function grantPresaleRole(address presale) external;
}

/**
 * @title PresaleFactory
 * @notice Factory for deploying presale rounds with integrated vesting and compliance tracking
 * @dev Aligns with Modul 2/5/6/13/15 specifications
 */
contract PresaleFactory is AccessControl {
    bytes32 public constant FACTORY_ADMIN_ROLE = keccak256("FACTORY_ADMIN_ROLE");

    // External contract references
    address public immutable feeSplitter;
    address public immutable timelockExecutor;

    // Presale tracking
    uint256 public presaleCount;
    mapping(uint256 => address) public rounds;
    mapping(address => address) public roundToVesting;
    mapping(address => bytes32) public roundToScheduleSalt;
    mapping(address => bytes32) public roundToComplianceHash;
    mapping(address => LPLockPlan) public roundToLPPlan;

    struct LPLockPlan {
        uint256 lockMonths;      // Minimum 12 months
        bytes32 dexId;           // DEX identifier (e.g., keccak256("pancakeswap"))
        uint256 liquidityPercent; // Percentage of raised funds for liquidity (BPS)
    }

    struct CreatePresaleParams {
        address projectToken;
        address paymentToken;
        uint256 softCap;
        uint256 hardCap;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        address projectOwner;
    }

    struct VestingParams {
        uint256 tgeUnlockBps;    // TGE unlock percentage (1000 = 10%)
        uint256 cliffDuration;    // Cliff duration in seconds
        uint256 vestingDuration;  // Linear vesting duration in seconds
    }

    // Events
    event PresaleCreated(
        uint256 indexed presaleId,
        address indexed round,
        address indexed vesting,
        bytes32 scheduleSalt,
        bytes32 complianceHash
    );
    event LPPlanRecorded(
        address indexed round,
        uint256 lockMonths,
        bytes32 dexId,
        uint256 liquidityPercent
    );
    event RolesGranted(address indexed vesting, address indexed rootSetter);

    // Errors
    error InvalidCapRange();
    error InvalidTimeRange();
    error InvalidContributionRange();
    error InsufficientLPLockDuration();
    error InvalidLiquidityPercent();
    error ZeroAddress();

    constructor(address _feeSplitter, address _timelockExecutor) {
        if (_feeSplitter == address(0)) revert ZeroAddress();
        if (_timelockExecutor == address(0)) revert ZeroAddress();

        feeSplitter = _feeSplitter;
        timelockExecutor = _timelockExecutor;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(FACTORY_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new presale round with integrated vesting
     * @dev Off-chain gates (KYC, SC Scan, Fee Payment, Admin Approval) are validated by backend
     * @param params Presale configuration parameters
     * @param vestingParams Vesting schedule configuration
     * @param lpPlan Liquidity lock plan (min 12 months)
     * @param complianceHash Optional hash of off-chain compliance data
     * @return round Address of deployed PresaleRound
     * @return vesting Address of deployed MerkleVesting
     */
    function createPresale(
        CreatePresaleParams calldata params,
        VestingParams calldata vestingParams,
        LPLockPlan calldata lpPlan,
        bytes32 complianceHash
    ) external onlyRole(FACTORY_ADMIN_ROLE) returns (address round, address vesting) {
        // Validate parameters
        _validatePresaleParams(params, lpPlan);

        // Generate unique schedule salt for this presale
        bytes32 scheduleSalt = keccak256(abi.encodePacked(
            presaleCount,
            block.timestamp,
            params.projectOwner,
            params.projectToken
        ));

        // Deploy MerkleVesting with factory as initial admin
        vesting = address(new MerkleVesting(
            params.projectToken,
            vestingParams.tgeUnlockBps,
            vestingParams.cliffDuration,
            vestingParams.vestingDuration,
            scheduleSalt,
            address(this)  // Factory is initial admin
        ));

        // Grant ADMIN_ROLE to timelock executor for setMerkleRoot
        bytes32 ADMIN_ROLE = keccak256("ADMIN_ROLE");
        MerkleVesting(vesting).grantRole(ADMIN_ROLE, timelockExecutor);

        // Revoke factory's admin role (optional - keep for now for flexibility)
        // MerkleVesting(vesting).revokeRole(DEFAULT_ADMIN_ROLE, address(this));

        // Deploy PresaleRound
        round = address(new PresaleRound(
            params.projectToken,
            params.paymentToken,
            params.softCap,
            params.hardCap,
            params.minContribution,
            params.maxContribution,
            params.startTime,
            params.endTime,
            feeSplitter,
            vesting,
            params.projectOwner,
            timelockExecutor  // Admin role for round
        ));

        // Grant ADMIN_ROLE to round for calling setMerkleRoot during finalizeSuccess
        MerkleVesting(vesting).grantRole(ADMIN_ROLE, round);

        // Grant PRESALE_ROLE on FeeSplitter to round for fee distribution
        IFeeSplitterExt(feeSplitter).grantPresaleRole(round);

        // Record mappings
        rounds[presaleCount] = round;
        roundToVesting[round] = vesting;
        roundToScheduleSalt[round] = scheduleSalt;
        roundToComplianceHash[round] = complianceHash;
        roundToLPPlan[round] = lpPlan;

        // Emit events
        emit PresaleCreated(presaleCount, round, vesting, scheduleSalt, complianceHash);
        emit LPPlanRecorded(round, lpPlan.lockMonths, lpPlan.dexId, lpPlan.liquidityPercent);
        emit RolesGranted(vesting, timelockExecutor);

        presaleCount++;

        return (round, vesting);
    }

    /**
     * @notice Validate presale parameters
     * @dev Internal validation logic
     */
    function _validatePresaleParams(
        CreatePresaleParams calldata params,
        LPLockPlan calldata lpPlan
    ) internal view {
        // Validate caps
        if (params.softCap >= params.hardCap) revert InvalidCapRange();
        if (params.softCap == 0) revert InvalidCapRange();

        // Validate time
        if (params.startTime >= params.endTime) revert InvalidTimeRange();
        if (params.startTime <= block.timestamp) revert InvalidTimeRange();

        // Validate contribution limits
        if (params.minContribution > params.maxContribution) revert InvalidContributionRange();
        if (params.minContribution == 0) revert InvalidContributionRange();

        // Validate LP lock (Modul 5: minimum 12 months)
        if (lpPlan.lockMonths < 12) revert InsufficientLPLockDuration();

        // Validate liquidity percent (reasonable range: 50-90%)
        if (lpPlan.liquidityPercent < 5000 || lpPlan.liquidityPercent > 9000) {
            revert InvalidLiquidityPercent();
        }

        // Validate addresses
        if (params.projectToken == address(0)) revert ZeroAddress();
        if (params.projectOwner == address(0)) revert ZeroAddress();
    }

    /**
     * @notice Get presale details
     * @param presaleId Presale ID
     */
    function getPresaleDetails(uint256 presaleId) external view returns (
        address round,
        address vesting,
        bytes32 scheduleSalt,
        bytes32 complianceHash,
        LPLockPlan memory lpPlan
    ) {
        round = rounds[presaleId];
        vesting = roundToVesting[round];
        scheduleSalt = roundToScheduleSalt[round];
        complianceHash = roundToComplianceHash[round];
        lpPlan = roundToLPPlan[round];
    }

    /**
     * @notice Get vesting address for a round
     */
    function getVestingForRound(address round) external view returns (address) {
        return roundToVesting[round];
    }

    /**
     * @notice Get schedule salt for a round
     */
    function getScheduleSalt(address round) external view returns (bytes32) {
        return roundToScheduleSalt[round];
    }
}

contracts/std-presale/PresaleRound.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IFeeSplitter {
    function distributeFeeNative() external payable;
    function distributeFeeERC20(address token, uint256 amount) external;
}

interface IMerkleVesting {
    function setMerkleRoot(bytes32 root, uint256 totalAllocated) external;
}

/**
 * @title PresaleRound
 * @notice Secure presale contract with refund-safe fee handling and scalable vesting
 * @dev v2.1 Compliant: Status sync, locked fee config, vault funding enforcement
 */
contract PresaleRound is ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // State Machine
    enum Status {
        UPCOMING,           // Not yet started
        ACTIVE,             // Accepting contributions
        ENDED,              // Time ended, awaiting finalization
        FINALIZED_SUCCESS,  // Soft cap met, funds distributed
        FINALIZED_FAILED,   // Soft cap not met, refunds active
        CANCELLED           // Admin cancelled, refunds active
    }

    struct FeeConfig {
        uint256 totalBps;          // Total fee in basis points (500 = 5%)
        uint256 treasuryBps;       // Treasury share (250 = 2.5%)
        uint256 referralPoolBps;   // Referral pool share (200 = 2%)
        uint256 sbtStakingBps;     // SBT staking share (50 = 0.5%)
    }

    // Immutable Config
    address public immutable projectToken;
    address public immutable paymentToken; // address(0) for native
    uint256 public immutable softCap;
    uint256 public immutable hardCap;
    uint256 public immutable minContribution;
    uint256 public immutable maxContribution;
    uint256 public immutable startTime;
    uint256 public immutable endTime;

    // External Contracts
    address public immutable feeSplitter;
    address public immutable vestingVault;
    address public immutable projectOwner;

    // Fee Config
    FeeConfig public feeConfig;

    // State
    Status public status;
    uint256 public totalRaised;
    uint256 public tgeTimestamp; // Set at finalization

    // Mappings
    mapping(address => uint256) public contributions;
    mapping(address => address) public referrers; // contributor => referrer

    // Events
    event Contributed(address indexed contributor, uint256 amount, address indexed referrer);
    event Refunded(address indexed contributor, uint256 amount);
    event FinalizedSuccess(uint256 totalRaised, uint256 feeAmount, uint256 tgeTimestamp);
    event FinalizedFailed(string reason);
    event Cancelled(string reason);
    event FeeConfigUpdated(FeeConfig newConfig);
    event StatusSynced(Status oldStatus, Status newStatus);

    // Errors
    error InvalidStatus();
    error SoftCapNotMet();
    error ContributionLimitExceeded();
    error RefundNotAvailable();
    error NoContribution();
    error BelowMinimum();
    error HardCapExceeded();
    error InvalidFeeConfig();
    error ConfigLocked();
    error InvalidAddress();
    error VestingFundingFailed();

    constructor(
        address _projectToken,
        address _paymentToken,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _minContribution,
        uint256 _maxContribution,
        uint256 _startTime,
        uint256 _endTime,
        address _feeSplitter,
        address _vestingVault,
        address _projectOwner,
        address _admin
    ) {
        if (_hardCap < _softCap) revert InvalidStatus();
        if (_endTime <= _startTime) revert InvalidStatus();
        if (_feeSplitter == address(0)) revert InvalidAddress();
        if (_vestingVault == address(0)) revert InvalidAddress();
        if (_projectOwner == address(0)) revert InvalidAddress();

        projectToken = _projectToken;
        paymentToken = _paymentToken;
        softCap = _softCap;
        hardCap = _hardCap;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
        startTime = _startTime;
        endTime = _endTime;
        feeSplitter = _feeSplitter;
        vestingVault = _vestingVault;
        projectOwner = _projectOwner;

        status = Status.UPCOMING;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);

        // Default fee config: 5% total (2.5% / 2% / 0.5%)
        feeConfig = FeeConfig({
            totalBps: 500,
            treasuryBps: 250,
            referralPoolBps: 200,
            sbtStakingBps: 50
        });
    }

    /**
     * @notice V2.1 PATCH: Sync status based on time/cap
     * @dev Prevents finalize deadlock if no tx after endTime
     */
    function _syncStatus() internal {
        Status oldStatus = status;

        if (block.timestamp >= startTime && status == Status.UPCOMING) {
            status = Status.ACTIVE;
        }

        if (status == Status.ACTIVE) {
            if (block.timestamp >= endTime || totalRaised >= hardCap) {
                status = Status.ENDED;
            }
        }

        if (oldStatus != status) {
            emit StatusSynced(oldStatus, status);
        }
    }

    /**
     * @notice Contribute to presale
     * @dev V2.1: Funds stay in escrow (100%), NO fee deduction here
     */
    function contribute(uint256 amount, address referrer) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
    {
        // V2.1 PATCH: Sync status before processing
        _syncStatus();

        if (status != Status.ACTIVE) revert InvalidStatus();

        uint256 newTotal = contributions[msg.sender] + amount;

        if (newTotal < minContribution) revert BelowMinimum();
        if (newTotal > maxContribution) revert ContributionLimitExceeded();
        if (totalRaised + amount > hardCap) revert HardCapExceeded();

        // Transfer payment (FULL AMOUNT to escrow)
        if (paymentToken == address(0)) {
            if (msg.value != amount) revert InvalidStatus();
        } else {
            IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        // Record contribution
        contributions[msg.sender] += amount;
        totalRaised += amount;

        // Record referrer (immutable per contributor)
        if (referrer != address(0) && referrers[msg.sender] == address(0)) {
            referrers[msg.sender] = referrer;
        }

        emit Contributed(msg.sender, amount, referrers[msg.sender]);
    }

    /**
     * @notice Finalize presale successfully (soft cap met)
     * @dev V2.1: Deducts fee & distributes. Funds vesting vault. Sets TGE.
     */
    function finalizeSuccess(
        bytes32 merkleRoot,
        uint256 totalVestingAllocation
    ) external onlyRole(ADMIN_ROLE) nonReentrant {
        // V2.1 PATCH: Sync status
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised < softCap) revert SoftCapNotMet();

        // Calculate fee
        uint256 feeAmount = (totalRaised * feeConfig.totalBps) / 10000;
        uint256 netAmount = totalRaised - feeAmount;

        // V2.1 PATCH: Fund vesting vault BEFORE setting root
        IERC20(projectToken).safeTransferFrom(
            projectOwner,
            vestingVault,
            totalVestingAllocation
        );

        // Set merkle root (will revert if vault underfunded)
        IMerkleVesting(vestingVault).setMerkleRoot(merkleRoot, totalVestingAllocation);

        // Distribute fee via FeeSplitter
        if (paymentToken == address(0)) {
            // Native token
            IFeeSplitter(feeSplitter).distributeFeeNative{value: feeAmount}();
        } else {
            // ERC20 token
            IERC20(paymentToken).forceApprove(feeSplitter, feeAmount);
            IFeeSplitter(feeSplitter).distributeFeeERC20(paymentToken, feeAmount);
        }

        // Transfer net amount to project owner
        if (paymentToken == address(0)) {
            (bool success, ) = projectOwner.call{value: netAmount}("");
            if (!success) revert VestingFundingFailed();
        } else {
            IERC20(paymentToken).safeTransfer(projectOwner, netAmount);
        }

        // Set TGE timestamp
        tgeTimestamp = block.timestamp;

        // Update status
        status = Status.FINALIZED_SUCCESS;

        emit FinalizedSuccess(totalRaised, feeAmount, tgeTimestamp);
    }

    /**
     * @notice Finalize presale as failed (soft cap not met)
     */
    function finalizeFailed(string calldata reason) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        // V2.1 PATCH: Sync status
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised >= softCap) revert InvalidStatus();

        status = Status.FINALIZED_FAILED;
        emit FinalizedFailed(reason);
    }

    /**
     * @notice Cancel presale (admin emergency)
     */
    function cancel(string calldata reason) external onlyRole(ADMIN_ROLE) {
        _syncStatus();

        if (status != Status.UPCOMING && status != Status.ACTIVE) {
            revert InvalidStatus();
        }

        status = Status.CANCELLED;
        emit Cancelled(reason);
    }

    /**
     * @notice Claim refund
     * @dev V2.1: ALWAYS accessible when FAILED | CANCELLED (NOT affected by pause)
     */
    function claimRefund() external nonReentrant {
        // V2.1 PATCH: Refund NOT blocked by pause
        if (status != Status.FINALIZED_FAILED && status != Status.CANCELLED) {
            revert RefundNotAvailable();
        }

        uint256 amount = contributions[msg.sender];
        if (amount == 0) revert NoContribution();

        contributions[msg.sender] = 0;

        // Transfer full contribution back (NO fee deduction)
        if (paymentToken == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            if (!success) revert VestingFundingFailed();
        } else {
            IERC20(paymentToken).safeTransfer(msg.sender, amount);
        }

        emit Refunded(msg.sender, amount);
    }

    /**
     * @notice Update fee configuration
     * @dev V2.1 PATCH: LOCKED after presale starts
     */
    function setFeeConfig(FeeConfig calldata newConfig) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        // V2.1 PATCH: Can only update if UPCOMING + no contributions + before start
        if (status != Status.UPCOMING) revert ConfigLocked();
        if (totalRaised > 0) revert ConfigLocked();
        if (block.timestamp >= startTime) revert ConfigLocked();

        // Validate total equals sum
        if (newConfig.totalBps != 
            newConfig.treasuryBps + 
            newConfig.referralPoolBps + 
            newConfig.sbtStakingBps
        ) {
            revert InvalidFeeConfig();
        }

        feeConfig = newConfig;
        emit FeeConfigUpdated(newConfig);
    }

    /**
     * @notice Pause contributions only
     * @dev V2.1: Refunds CANNOT be paused
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Get contribution info
     */
    function getContributionInfo(address contributor) 
        external 
        view 
        returns (
            uint256 contribution,
            address referrer
        ) 
    {
        return (
            contributions[contributor],
            referrers[contributor]
        );
    }

    /**
     * @notice Get presale info
     */
    function getPresaleInfo() 
        external 
        view 
        returns (
            Status _status,
            uint256 _totalRaised,
            uint256 _softCap,
            uint256 _hardCap,
            uint256 _startTime,
            uint256 _endTime,
            uint256 _tgeTimestamp
        ) 
    {
        return (
            status,
            totalRaised,
            softCap,
            hardCap,
            startTime,
            endTime,
            tgeTimestamp
        );
    }
}

contracts/token/StandardERC20Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StandardERC20Token
 * @notice Standard ERC20 token template for platform token creation
 * @dev Fixed supply token with optional anti-bot features
 */
contract StandardERC20Token is ERC20, Ownable {
    uint8 private immutable _decimals;

    // Anti-bot configuration
    bool public antiBotEnabled;
    uint256 public maxTransactionAmount;
    uint256 public maxWalletAmount;
    uint256 public tradingCooldown;
    uint256 public tradingEnabledAt;

    mapping(address => uint256) private _lastTransferTimestamp;
    mapping(address => bool) public isExcludedFromLimits;

    event AntiBotConfigured(
        uint256 maxTransactionAmount,
        uint256 maxWalletAmount,
        uint256 tradingCooldown
    );
    event TradingEnabled(uint256 timestamp);
    event ExcludedFromLimits(address indexed account, bool excluded);

    /**
     * @notice Creates a new standard ERC20 token
     * @param name_ Token name
     * @param symbol_ Token symbol
     * @param decimals_ Token decimals
     * @param totalSupply_ Total token supply (minted to creator)
     * @param creator_ Address to receive initial supply and ownership
     * @param antiBotConfig_ Anti-bot configuration [maxTxPercent, maxWalletPercent, cooldownSeconds]
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 totalSupply_,
        address creator_,
        uint256[3] memory antiBotConfig_
    ) ERC20(name_, symbol_) Ownable(creator_) {
        require(creator_ != address(0), "Creator cannot be zero address");
        require(totalSupply_ > 0, "Total supply must be greater than 0");

        _decimals = decimals_;

        // Mint total supply to creator
        _mint(creator_, totalSupply_);

        // Configure anti-bot if provided
        if (antiBotConfig_[0] > 0 || antiBotConfig_[1] > 0 || antiBotConfig_[2] > 0) {
            _configureAntiBot(totalSupply_, antiBotConfig_);
        }

        // Exclude creator and contract from limits
        isExcludedFromLimits[creator_] = true;
        isExcludedFromLimits[address(this)] = true;

        // Trading disabled by default, owner must enable
        tradingEnabledAt = 0;
    }

    /**
     * @notice Returns the number of decimals
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @notice Enables trading (can only be called once)
     */
    function enableTrading() external onlyOwner {
        require(tradingEnabledAt == 0, "Trading already enabled");
        tradingEnabledAt = block.timestamp;
        emit TradingEnabled(block.timestamp);
    }

    /**
     * @notice Updates anti-bot configuration
     * @param maxTxPercent Maximum transaction as percentage of total supply (basis points)
     * @param maxWalletPercent Maximum wallet holding as percentage of total supply (basis points)
     * @param cooldownSeconds Cooldown period between transfers
     */
    function updateAntiBotConfig(
        uint256 maxTxPercent,
        uint256 maxWalletPercent,
        uint256 cooldownSeconds
    ) external onlyOwner {
        uint256 totalSupply_ = totalSupply();
        uint256[3] memory config = [maxTxPercent, maxWalletPercent, cooldownSeconds];
        _configureAntiBot(totalSupply_, config);
    }

    /**
     * @notice Excludes/includes an address from anti-bot limits
     */
    function setExcludedFromLimits(address account, bool excluded) external onlyOwner {
        isExcludedFromLimits[account] = excluded;
        emit ExcludedFromLimits(account, excluded);
    }

    /**
     * @notice Disables anti-bot permanently
     */
    function disableAntiBot() external onlyOwner {
        antiBotEnabled = false;
        maxTransactionAmount = 0;
        maxWalletAmount = 0;
        tradingCooldown = 0;
    }

    /**
     * @dev Override transfer to include anti-bot checks
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        // Skip checks for minting/burning and excluded addresses
        if (from == address(0) || to == address(0) || 
            isExcludedFromLimits[from] || isExcludedFromLimits[to]) {
            super._update(from, to, amount);
            return;
        }

        // Check if trading is enabled
        require(tradingEnabledAt > 0, "Trading not enabled yet");

        // Apply anti-bot checks if enabled
        if (antiBotEnabled) {
            // Check max transaction amount
            if (maxTransactionAmount > 0) {
                require(amount <= maxTransactionAmount, "Exceeds max transaction amount");
            }

            // Check max wallet amount
            if (maxWalletAmount > 0 && to != address(0)) {
                require(
                    balanceOf(to) + amount <= maxWalletAmount,
                    "Exceeds max wallet amount"
                );
            }

            // Check trading cooldown
            if (tradingCooldown > 0) {
                require(
                    block.timestamp >= _lastTransferTimestamp[from] + tradingCooldown,
                    "Transfer cooldown active"
                );
                _lastTransferTimestamp[from] = block.timestamp;
            }
        }

        super._update(from, to, amount);
    }

    /**
     * @dev Internal function to configure anti-bot settings
     */
    function _configureAntiBot(
        uint256 totalSupply_,
        uint256[3] memory config
    ) private {
        antiBotEnabled = true;

        // config[0] = maxTxPercent (basis points, e.g., 100 = 1%)
        // config[1] = maxWalletPercent (basis points)
        // config[2] = cooldownSeconds

        if (config[0] > 0) {
            require(config[0] <= 10000, "Max tx percent must be <= 100%");
            maxTransactionAmount = (totalSupply_ * config[0]) / 10000;
        }

        if (config[1] > 0) {
            require(config[1] <= 10000, "Max wallet percent must be <= 100%");
            maxWalletAmount = (totalSupply_ * config[1]) / 10000;
        }

        tradingCooldown = config[2];

        emit AntiBotConfigured(maxTransactionAmount, maxWalletAmount, tradingCooldown);
    }
}

contracts/token/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./StandardERC20Token.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title TokenFactory
 * @notice Factory contract for deploying standard ERC20 tokens with creation fees
 * @dev Manages token deployment, fee collection, and token registry
 */
contract TokenFactory is Ownable, ReentrancyGuard {
    // Creation fee per network (paid in native token)
    uint256 public creationFee;

    // Treasury wallet receiving 100% of creation fees
    address payable public treasuryWallet;

    // Registry of created tokens
    address[] public allTokens;
    mapping(address => address[]) public creatorTokens;
    mapping(address => bool) public isFromFactory;
    mapping(address => TokenInfo) public tokenInfo;

    struct TokenInfo {
        address creator;
        string name;
        string symbol;
        uint8 decimals;
        uint256 totalSupply;
        uint256 createdAt;
        uint256 feePaid;
    }

    event TokenCreated(
        address indexed tokenAddress,
        address indexed creator,
        string name,
        string symbol,
        uint8 decimals,
        uint256 totalSupply,
        uint256 feePaid,
        uint256 timestamp
    );

    event CreationFeeUpdated(uint256 oldFee, uint256 newFee);
    event TreasuryWalletUpdated(address indexed oldWallet, address indexed newWallet);
    event FeesWithdrawn(address indexed to, uint256 amount);

    /**
     * @notice Initializes the TokenFactory
     * @param initialCreationFee_ Initial creation fee in wei
     * @param treasuryWallet_ Treasury wallet address
     * @param initialOwner_ Contract owner address
     */
    constructor(
        uint256 initialCreationFee_,
        address payable treasuryWallet_,
        address initialOwner_
    ) Ownable(initialOwner_) {
        require(treasuryWallet_ != address(0), "Treasury cannot be zero address");
        require(initialOwner_ != address(0), "Owner cannot be zero address");

        creationFee = initialCreationFee_;
        treasuryWallet = treasuryWallet_;
    }

    /**
     * @notice Creates a new standard ERC20 token
     * @param name_ Token name
     * @param symbol_ Token symbol
     * @param decimals_ Token decimals
     * @param totalSupply_ Total supply (in base units)
     * @param antiBotConfig_ Anti-bot settings [maxTxPercent, maxWalletPercent, cooldownSeconds]
     * @return tokenAddress Address of the newly created token
     */
    function createToken(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 totalSupply_,
        uint256[3] memory antiBotConfig_
    ) external payable nonReentrant returns (address tokenAddress) {
        require(msg.value >= creationFee, "Insufficient creation fee");
        require(bytes(name_).length > 0, "Name cannot be empty");
        require(bytes(symbol_).length > 0, "Symbol cannot be empty");
        require(totalSupply_ > 0, "Total supply must be greater than 0");

        // Deploy new token with caller as creator
        StandardERC20Token newToken = new StandardERC20Token(
            name_,
            symbol_,
            decimals_,
            totalSupply_,
            msg.sender,
            antiBotConfig_
        );

        tokenAddress = address(newToken);

        // Register token
        allTokens.push(tokenAddress);
        creatorTokens[msg.sender].push(tokenAddress);
        isFromFactory[tokenAddress] = true;

        tokenInfo[tokenAddress] = TokenInfo({
            creator: msg.sender,
            name: name_,
            symbol: symbol_,
            decimals: decimals_,
            totalSupply: totalSupply_,
            createdAt: block.timestamp,
            feePaid: msg.value
        });

        // Transfer only creation fee to treasury (not the full msg.value)
        (bool success, ) = treasuryWallet.call{value: creationFee}("");
        require(success, "Fee transfer failed");

        emit TokenCreated(
            tokenAddress,
            msg.sender,
            name_,
            symbol_,
            decimals_,
            totalSupply_,
            creationFee, // Only log the actual fee, not msg.value
            block.timestamp
        );

        // Refund excess payment
        if (msg.value > creationFee) {
            uint256 refund = msg.value - creationFee;
            (bool refundSuccess, ) = payable(msg.sender).call{value: refund}("");
            require(refundSuccess, "Refund failed");
        }

        return tokenAddress;
    }

    /**
     * @notice Updates the creation fee
     * @param newFee_ New fee amount in wei
     */
    function updateCreationFee(uint256 newFee_) external onlyOwner {
        uint256 oldFee = creationFee;
        creationFee = newFee_;
        emit CreationFeeUpdated(oldFee, newFee_);
    }

    /**
     * @notice Updates the treasury wallet
     * @param newTreasury_ New treasury wallet address
     */
    function updateTreasuryWallet(address payable newTreasury_) external onlyOwner {
        require(newTreasury_ != address(0), "Treasury cannot be zero address");
        address oldWallet = treasuryWallet;
        treasuryWallet = newTreasury_;
        emit TreasuryWalletUpdated(oldWallet, newTreasury_);
    }

    /**
     * @notice Gets all tokens created by a specific creator
     * @param creator_ Creator address
     * @return Array of token addresses
     */
    function getCreatorTokens(address creator_) external view returns (address[] memory) {
        return creatorTokens[creator_];
    }

    /**
     * @notice Gets the total number of tokens created
     * @return Total token count
     */
    function getTotalTokensCreated() external view returns (uint256) {
        return allTokens.length;
    }

    /**
     * @notice Gets a token address by index
     * @param index_ Token index
     * @return Token address
     */
    function getTokenAtIndex(uint256 index_) external view returns (address) {
        require(index_ < allTokens.length, "Index out of bounds");
        return allTokens[index_];
    }

    /**
     * @notice Withdraws any accidentally sent tokens/ETH (emergency only)
     * @param to_ Recipient address
     */
    function emergencyWithdraw(address payable to_) external onlyOwner {
        require(to_ != address(0), "Recipient cannot be zero address");
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool success, ) = to_.call{value: balance}("");
            require(success, "Withdrawal failed");
            emit FeesWithdrawn(to_, balance);
        }
    }

    /**
     * @notice Allows contract to receive ETH
     */
    receive() external payable {}
}

