 * those functions `private`, and then adding `external` `nonReentrant` entry
 * points to them.
 *
 * TIP: If EIP-1153 (transient storage) is available on the chain you're deploying at,
 * consider using {ReentrancyGuardTransient} instead.
 *
 * TIP: If you would like to learn more about reentrancy and alternative ways
 * to protect against it, check out our blog post
 * https://blog.openzeppelin.com/reentrancy-after-istanbul/[Reentrancy After Istanbul].
 */
abstract contract ReentrancyGuard {
    // Booleans are more expensive than uint256 or any type that takes up a full
    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/std-presale/MerkleVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title MerkleVesting
 * @notice Scalable token vesting using Merkle tree for allocation proofs
 * @dev Prevents cross-round proof replay via salted leaf encoding
 */
contract MerkleVesting is ReentrancyGuard, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // Immutable parameters
    address public immutable token;
    uint256 public immutable tgeUnlockBps;     // Basis points unlocked at TGE (1000 = 10%)
    uint256 public immutable cliffDuration;     // Seconds after TGE
    uint256 public immutable vestingDuration;   // Seconds of linear vesting
    bytes32 public immutable scheduleSalt;       // Unique salt for this schedule (hashed)

    // State
    bytes32 public merkleRoot;
    uint256 public tgeTimestamp;
    uint256 public totalAllocated;

    // Per-user claimed amounts
    mapping(address => uint256) public claimed;

    // Events
    event MerkleRootSet(bytes32 indexed merkleRoot, uint256 tgeTimestamp, uint256 totalAllocated);
    event Claimed(address indexed beneficiary, uint256 amount);

    // Errors
    error RootAlreadySet();
    error RootNotSet();
    error InvalidProof();
    error NothingToClaim();
    error InsufficientVaultBalance();
    error ZeroAllocation();

    constructor(
        address _token,
        uint256 _tgeUnlockBps,
        uint256 _cliffDuration,
        uint256 _vestingDuration,
        bytes32 _scheduleSalt,
        address _admin
    ) {
        token = _token;
        tgeUnlockBps = _tgeUnlockBps;
        cliffDuration = _cliffDuration;
        vestingDuration = _vestingDuration;
        scheduleSalt = _scheduleSalt;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
    }

    /**
     * @notice Set Merkle root (called once after presale finalization)
     * @param _merkleRoot Root of allocation Merkle tree
     * @param _totalAllocated Total tokens allocated (for vault balance check)
     */
    function setMerkleRoot(bytes32 _merkleRoot, uint256 _totalAllocated) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        if (merkleRoot != bytes32(0)) revert RootAlreadySet();
        if (_merkleRoot == bytes32(0)) revert InvalidProof();
        if (_totalAllocated == 0) revert ZeroAllocation();

        // CRITICAL: Ensure vault is funded before enabling claims
        uint256 vaultBalance = IERC20(token).balanceOf(address(this));
        if (vaultBalance < _totalAllocated) revert InsufficientVaultBalance();

        merkleRoot = _merkleRoot;
        tgeTimestamp = block.timestamp;
        totalAllocated = _totalAllocated;

        emit MerkleRootSet(_merkleRoot, tgeTimestamp, _totalAllocated);
    }

    /**
     * @notice Claim vested tokens with Merkle proof
     * @param totalAllocation Total tokens allocated to beneficiary
     * @param proof Merkle proof
     */
    function claim(uint256 totalAllocation, bytes32[] calldata proof) 
        external 
        nonReentrant 
    {
        if (merkleRoot == bytes32(0)) revert RootNotSet();

        // V2.1 PATCH: Salted leaf encoding to prevent cross-round replay
        bytes32 leaf = keccak256(abi.encodePacked(
            address(this),          // Contract address
            block.chainid,          // Chain ID
            scheduleSalt,           // Schedule salt (already bytes32)
            msg.sender,             // Beneficiary
            totalAllocation         // Allocation amount
        ));

        // Verify Merkle proof
        if (!MerkleProof.verify(proof, merkleRoot, leaf)) {
            revert InvalidProof();
        }

        // Calculate claimable
        uint256 claimable = _calculateClaimable(totalAllocation);
        uint256 alreadyClaimed = claimed[msg.sender];

        if (claimable <= alreadyClaimed) revert NothingToClaim();

        uint256 toClaim = claimable - alreadyClaimed;

        // Update claimed
        claimed[msg.sender] = claimable;

        // Transfer tokens
        IERC20(token).safeTransfer(msg.sender, toClaim);

        emit Claimed(msg.sender, toClaim);
    }

    /**
     * @notice Calculate claimable amount
     * @dev TGE unlock + cliff + linear vesting
     */
    function _calculateClaimable(uint256 totalAllocation) 
        internal 
        view 
        returns (uint256) 
    {
        // TGE amount
        uint256 tgeAmount = (totalAllocation * tgeUnlockBps) / 10000;

        // Before cliff: only TGE unlocked
        uint256 cliffEnd = tgeTimestamp + cliffDuration;
        if (block.timestamp < cliffEnd) {
            return tgeAmount;
        }

        // After vesting ends: fully unlocked
        uint256 vestingEnd = cliffEnd + vestingDuration;
        if (block.timestamp >= vestingEnd) {
            return totalAllocation;
        }

        // Linear vesting
        uint256 vestedAmount = totalAllocation - tgeAmount;
        uint256 elapsed = block.timestamp - cliffEnd;
        uint256 vestedSoFar = (vestedAmount * elapsed) / vestingDuration;

        return tgeAmount + vestedSoFar;
    }

    /**
     * @notice View: Get claimable amount for beneficiary
     * @param beneficiary Beneficiary address
     * @param totalAllocation Total allocation for beneficiary
     */
    function getClaimable(address beneficiary, uint256 totalAllocation) 
        external 
        view 
        returns (uint256) 
    {
        if (merkleRoot == bytes32(0)) return 0;

        uint256 claimable = _calculateClaimable(totalAllocation);
        uint256 alreadyClaimed = claimed[beneficiary];

        return claimable > alreadyClaimed ? claimable - alreadyClaimed : 0;
    }

    /**
     * @notice View: Get vesting info
     */
    function getVestingInfo() external view returns (
        uint256 _tgeUnlockBps,
        uint256 _cliffDuration,
        uint256 _vestingDuration,
        uint256 _tgeTimestamp,
        bytes32 _merkleRoot
    ) {
        return (
            tgeUnlockBps,
            cliffDuration,
            vestingDuration,
            tgeTimestamp,
            merkleRoot
        );
    }
}

contracts/std-presale/PresaleFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./PresaleRound.sol";
import "./MerkleVesting.sol";

// Extended interface for FeeSplitter (includes role management)
interface IFeeSplitterExt {
    function grantPresaleRole(address presale) external;
}

/**
 * @title PresaleFactory
 * @notice Factory for deploying presale rounds with integrated vesting and compliance tracking
 * @dev Aligns with Modul 2/5/6/13/15 specifications
 */
contract PresaleFactory is AccessControl {
    bytes32 public constant FACTORY_ADMIN_ROLE = keccak256("FACTORY_ADMIN_ROLE");

    // External contract references
    address public immutable feeSplitter;
    address public immutable timelockExecutor;

    // Presale tracking
    uint256 public presaleCount;
    mapping(uint256 => address) public rounds;
    mapping(address => address) public roundToVesting;
    mapping(address => bytes32) public roundToScheduleSalt;
    mapping(address => bytes32) public roundToComplianceHash;
    mapping(address => LPLockPlan) public roundToLPPlan;

    struct LPLockPlan {
        uint256 lockMonths;      // Minimum 12 months
        bytes32 dexId;           // DEX identifier (e.g., keccak256("pancakeswap"))
        uint256 liquidityPercent; // Percentage of raised funds for liquidity (BPS)
    }

    struct CreatePresaleParams {
        address projectToken;
        address paymentToken;
        uint256 softCap;
        uint256 hardCap;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        address projectOwner;
    }

    struct VestingParams {
        uint256 tgeUnlockBps;    // TGE unlock percentage (1000 = 10%)
        uint256 cliffDuration;    // Cliff duration in seconds
        uint256 vestingDuration;  // Linear vesting duration in seconds
    }

    // Events
    event PresaleCreated(
        uint256 indexed presaleId,
        address indexed round,
        address indexed vesting,
        bytes32 scheduleSalt,
        bytes32 complianceHash
    );
    event LPPlanRecorded(
        address indexed round,
        uint256 lockMonths,
        bytes32 dexId,
        uint256 liquidityPercent
    );
    event RolesGranted(address indexed vesting, address indexed rootSetter);

    // Errors
    error InvalidCapRange();
    error InvalidTimeRange();
    error InvalidContributionRange();
    error InsufficientLPLockDuration();
    error InvalidLiquidityPercent();
    error ZeroAddress();

    constructor(address _feeSplitter, address _timelockExecutor) {
        if (_feeSplitter == address(0)) revert ZeroAddress();
        if (_timelockExecutor == address(0)) revert ZeroAddress();

        feeSplitter = _feeSplitter;
        timelockExecutor = _timelockExecutor;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(FACTORY_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new presale round with integrated vesting
     * @dev Off-chain gates (KYC, SC Scan, Fee Payment, Admin Approval) are validated by backend
     * @param params Presale configuration parameters
     * @param vestingParams Vesting schedule configuration
     * @param lpPlan Liquidity lock plan (min 12 months)
     * @param complianceHash Optional hash of off-chain compliance data
     * @return round Address of deployed PresaleRound
     * @return vesting Address of deployed MerkleVesting
     */
    function createPresale(
        CreatePresaleParams calldata params,
        VestingParams calldata vestingParams,
        LPLockPlan calldata lpPlan,
        bytes32 complianceHash
    ) external onlyRole(FACTORY_ADMIN_ROLE) returns (address round, address vesting) {
        // Validate parameters
        _validatePresaleParams(params, lpPlan);

        // Generate unique schedule salt for this presale
        bytes32 scheduleSalt = keccak256(abi.encodePacked(
            presaleCount,
            block.timestamp,
            params.projectOwner,
            params.projectToken
        ));

        // Deploy MerkleVesting with factory as initial admin
        vesting = address(new MerkleVesting(
            params.projectToken,
            vestingParams.tgeUnlockBps,
            vestingParams.cliffDuration,
            vestingParams.vestingDuration,
            scheduleSalt,
            address(this)  // Factory is initial admin
        ));

        // Grant ADMIN_ROLE to timelock executor for setMerkleRoot
        bytes32 ADMIN_ROLE = keccak256("ADMIN_ROLE");
        MerkleVesting(vesting).grantRole(ADMIN_ROLE, timelockExecutor);

        // Revoke factory's admin role (optional - keep for now for flexibility)
        // MerkleVesting(vesting).revokeRole(DEFAULT_ADMIN_ROLE, address(this));

        // Deploy PresaleRound
        round = address(new PresaleRound(
            params.projectToken,
            params.paymentToken,
            params.softCap,
            params.hardCap,
            params.minContribution,
            params.maxContribution,
            params.startTime,
            params.endTime,
            feeSplitter,
            vesting,
            params.projectOwner,
            timelockExecutor  // Admin role for round
        ));

        // Grant ADMIN_ROLE to round for calling setMerkleRoot during finalizeSuccess
        MerkleVesting(vesting).grantRole(ADMIN_ROLE, round);

        // Grant PRESALE_ROLE on FeeSplitter to round for fee distribution
        IFeeSplitterExt(feeSplitter).grantPresaleRole(round);

        // Record mappings
        rounds[presaleCount] = round;
        roundToVesting[round] = vesting;
        roundToScheduleSalt[round] = scheduleSalt;
        roundToComplianceHash[round] = complianceHash;
        roundToLPPlan[round] = lpPlan;

        // Emit events
        emit PresaleCreated(presaleCount, round, vesting, scheduleSalt, complianceHash);
        emit LPPlanRecorded(round, lpPlan.lockMonths, lpPlan.dexId, lpPlan.liquidityPercent);
        emit RolesGranted(vesting, timelockExecutor);

        presaleCount++;

        return (round, vesting);
    }

    /**
     * @notice Validate presale parameters
     * @dev Internal validation logic
     */
    function _validatePresaleParams(
        CreatePresaleParams calldata params,
        LPLockPlan calldata lpPlan
    ) internal view {
        // Validate caps
        if (params.softCap >= params.hardCap) revert InvalidCapRange();
        if (params.softCap == 0) revert InvalidCapRange();

        // Validate time
        if (params.startTime >= params.endTime) revert InvalidTimeRange();
        if (params.startTime <= block.timestamp) revert InvalidTimeRange();

        // Validate contribution limits
        if (params.minContribution > params.maxContribution) revert InvalidContributionRange();
        if (params.minContribution == 0) revert InvalidContributionRange();

        // Validate LP lock (Modul 5: minimum 12 months)
        if (lpPlan.lockMonths < 12) revert InsufficientLPLockDuration();

        // Validate liquidity percent (reasonable range: 50-90%)
        if (lpPlan.liquidityPercent < 5000 || lpPlan.liquidityPercent > 9000) {
            revert InvalidLiquidityPercent();
        }

        // Validate addresses
        if (params.projectToken == address(0)) revert ZeroAddress();
        if (params.projectOwner == address(0)) revert ZeroAddress();
    }

    /**
     * @notice Get presale details
     * @param presaleId Presale ID
     */
    function getPresaleDetails(uint256 presaleId) external view returns (
        address round,
        address vesting,
        bytes32 scheduleSalt,
        bytes32 complianceHash,
        LPLockPlan memory lpPlan
    ) {
        round = rounds[presaleId];
        vesting = roundToVesting[round];
        scheduleSalt = roundToScheduleSalt[round];
        complianceHash = roundToComplianceHash[round];
        lpPlan = roundToLPPlan[round];
    }

    /**
     * @notice Get vesting address for a round
     */
    function getVestingForRound(address round) external view returns (address) {
        return roundToVesting[round];
    }

    /**
     * @notice Get schedule salt for a round
     */
    function getScheduleSalt(address round) external view returns (bytes32) {
        return roundToScheduleSalt[round];
    }
}

contracts/std-presale/PresaleRound.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IFeeSplitter {
    function distributeFeeNative() external payable;
    function distributeFeeERC20(address token, uint256 amount) external;
}

interface IMerkleVesting {
    function setMerkleRoot(bytes32 root, uint256 totalAllocated) external;
}

/**
 * @title PresaleRound
 * @notice Secure presale contract with refund-safe fee handling and scalable vesting
 * @dev v2.1 Compliant: Status sync, locked fee config, vault funding enforcement
 */
contract PresaleRound is ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // State Machine
    enum Status {
        UPCOMING,           // Not yet started
        ACTIVE,             // Accepting contributions
        ENDED,              // Time ended, awaiting finalization
        FINALIZED_SUCCESS,  // Soft cap met, funds distributed
        FINALIZED_FAILED,   // Soft cap not met, refunds active
        CANCELLED           // Admin cancelled, refunds active
    }

    struct FeeConfig {
        uint256 totalBps;          // Total fee in basis points (500 = 5%)
        uint256 treasuryBps;       // Treasury share (250 = 2.5%)
        uint256 referralPoolBps;   // Referral pool share (200 = 2%)
        uint256 sbtStakingBps;     // SBT staking share (50 = 0.5%)
    }

    // Immutable Config
    address public immutable projectToken;
    address public immutable paymentToken; // address(0) for native
    uint256 public immutable softCap;
    uint256 public immutable hardCap;
    uint256 public immutable minContribution;
    uint256 public immutable maxContribution;
    uint256 public immutable startTime;
    uint256 public immutable endTime;

    // External Contracts
    address public immutable feeSplitter;
    address public immutable vestingVault;
    address public immutable projectOwner;

    // Fee Config
    FeeConfig public feeConfig;

    // State
    Status public status;
    uint256 public totalRaised;
    uint256 public tgeTimestamp; // Set at finalization

    // Mappings
    mapping(address => uint256) public contributions;
    mapping(address => address) public referrers; // contributor => referrer

    // Events
    event Contributed(address indexed contributor, uint256 amount, address indexed referrer);
    event Refunded(address indexed contributor, uint256 amount);
    event FinalizedSuccess(uint256 totalRaised, uint256 feeAmount, uint256 tgeTimestamp);
    event FinalizedFailed(string reason);
    event Cancelled(string reason);
    event FeeConfigUpdated(FeeConfig newConfig);
    event StatusSynced(Status oldStatus, Status newStatus);
    event UnsoldTokensBurned(uint256 amount);

    // Errors
    error InvalidStatus();
    error SoftCapNotMet();
    error ContributionLimitExceeded();
    error RefundNotAvailable();
    error NoContribution();
    error BelowMinimum();
    error HardCapExceeded();
    error InvalidFeeConfig();
    error ConfigLocked();
    error InvalidAddress();
    error VestingFundingFailed();

    constructor(
        address _projectToken,
        address _paymentToken,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _minContribution,
        uint256 _maxContribution,
        uint256 _startTime,
        uint256 _endTime,
        address _feeSplitter,
        address _vestingVault,
        address _projectOwner,
        address _admin
    ) {
        if (_hardCap < _softCap) revert InvalidStatus();
        if (_endTime <= _startTime) revert InvalidStatus();
        if (_feeSplitter == address(0)) revert InvalidAddress();
        if (_vestingVault == address(0)) revert InvalidAddress();
        if (_projectOwner == address(0)) revert InvalidAddress();

        projectToken = _projectToken;
        paymentToken = _paymentToken;
        softCap = _softCap;
        hardCap = _hardCap;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
        startTime = _startTime;
        endTime = _endTime;
        feeSplitter = _feeSplitter;
        vestingVault = _vestingVault;
        projectOwner = _projectOwner;

        status = Status.UPCOMING;

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);

        // Default fee config: 5% total (2.5% / 2% / 0.5%)
        feeConfig = FeeConfig({
            totalBps: 500,
            treasuryBps: 250,
            referralPoolBps: 200,
            sbtStakingBps: 50
        });
    }

    /**
     * @notice V2.1 PATCH: Sync status based on time/cap
     * @dev Prevents finalize deadlock if no tx after endTime
     */
    function _syncStatus() internal {
        Status oldStatus = status;

        if (block.timestamp >= startTime && status == Status.UPCOMING) {
            status = Status.ACTIVE;
        }

        if (status == Status.ACTIVE) {
            if (block.timestamp >= endTime || totalRaised >= hardCap) {
                status = Status.ENDED;
            }
        }

        if (oldStatus != status) {
            emit StatusSynced(oldStatus, status);
        }
    }

    /**
     * @notice Contribute to presale
     * @dev V2.1: Funds stay in escrow (100%), NO fee deduction here
     */
    function contribute(uint256 amount, address referrer) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
    {
        // V2.1 PATCH: Sync status before processing
        _syncStatus();

        if (status != Status.ACTIVE) revert InvalidStatus();

        uint256 newTotal = contributions[msg.sender] + amount;

        if (newTotal < minContribution) revert BelowMinimum();
        if (newTotal > maxContribution) revert ContributionLimitExceeded();
        if (totalRaised + amount > hardCap) revert HardCapExceeded();

        // Transfer payment (FULL AMOUNT to escrow)
        if (paymentToken == address(0)) {
            if (msg.value != amount) revert InvalidStatus();
        } else {
            IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        // Record contribution
        contributions[msg.sender] += amount;
        totalRaised += amount;

        // Record referrer (immutable per contributor)
        if (referrer != address(0) && referrers[msg.sender] == address(0)) {
            referrers[msg.sender] = referrer;
        }

        emit Contributed(msg.sender, amount, referrers[msg.sender]);
    }

    /**
     * @notice Finalize presale successfully (soft cap met)
     * @dev V2.2: Deducts fee & distributes. Funds vesting vault. Burns unsold tokens. Sets TGE.
     * @param merkleRoot The merkle root for vesting claims
     * @param totalVestingAllocation Total tokens allocated for investor vesting
     * @param totalTokensForSale Total tokens that were offered for sale
     */
    function finalizeSuccess(
        bytes32 merkleRoot,
        uint256 totalVestingAllocation,
        uint256 totalTokensForSale
    ) external onlyRole(ADMIN_ROLE) nonReentrant {
        // V2.1 PATCH: Sync status
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised < softCap) revert SoftCapNotMet();

        // Calculate fee
        uint256 feeAmount = (totalRaised * feeConfig.totalBps) / 10000;
        uint256 netAmount = totalRaised - feeAmount;

        // V2.1 PATCH: Fund vesting vault BEFORE setting root
        IERC20(projectToken).safeTransferFrom(
            projectOwner,
            vestingVault,
            totalVestingAllocation
        );

        // Set merkle root (will revert if vault underfunded)
        IMerkleVesting(vestingVault).setMerkleRoot(merkleRoot, totalVestingAllocation);

        // Distribute fee via FeeSplitter
        if (paymentToken == address(0)) {
            // Native token
            IFeeSplitter(feeSplitter).distributeFeeNative{value: feeAmount}();
        } else {
            // ERC20 token
            IERC20(paymentToken).forceApprove(feeSplitter, feeAmount);
            IFeeSplitter(feeSplitter).distributeFeeERC20(paymentToken, feeAmount);
        }

        // Transfer net amount to project owner
        if (paymentToken == address(0)) {
            (bool success, ) = projectOwner.call{value: netAmount}("");
            if (!success) revert VestingFundingFailed();
        } else {
            IERC20(paymentToken).safeTransfer(projectOwner, netAmount);
        }

        // V2.2: Auto-burn unsold tokens (anti-dump protection)
        // If presale didn't reach hardcap, unsold tokens are burned
        if (totalRaised < hardCap && totalTokensForSale > 0) {
            // Calculate unsold tokens proportionally:
            // unsoldTokens = totalTokensForSale * (hardCap - totalRaised) / hardCap
            uint256 unsoldTokens = (totalTokensForSale * (hardCap - totalRaised)) / hardCap;

            if (unsoldTokens > 0) {
                // Transfer unsold tokens from projectOwner to burn address
                IERC20(projectToken).safeTransferFrom(
                    projectOwner,
                    address(0xdEaD),  // Standard burn address
                    unsoldTokens
                );
                emit UnsoldTokensBurned(unsoldTokens);
            }
        }

        // Set TGE timestamp
        tgeTimestamp = block.timestamp;

        // Update status
        status = Status.FINALIZED_SUCCESS;

        emit FinalizedSuccess(totalRaised, feeAmount, tgeTimestamp);
    }

    /**
     * @notice Finalize presale as failed (soft cap not met)
     */
    function finalizeFailed(string calldata reason) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        // V2.1 PATCH: Sync status
        _syncStatus();

        if (status != Status.ENDED) revert InvalidStatus();
        if (totalRaised >= softCap) revert InvalidStatus();

        status = Status.FINALIZED_FAILED;
        emit FinalizedFailed(reason);
    }

    /**
     * @notice Cancel presale (admin emergency)
     */
    function cancel(string calldata reason) external onlyRole(ADMIN_ROLE) {
        _syncStatus();

        if (status != Status.UPCOMING && status != Status.ACTIVE) {
            revert InvalidStatus();
        }

        status = Status.CANCELLED;
        emit Cancelled(reason);
    }

    /**
     * @notice Claim refund
     * @dev V2.1: ALWAYS accessible when FAILED | CANCELLED (NOT affected by pause)
     */
    function claimRefund() external nonReentrant {
        // V2.1 PATCH: Refund NOT blocked by pause
        if (status != Status.FINALIZED_FAILED && status != Status.CANCELLED) {
            revert RefundNotAvailable();
        }

        uint256 amount = contributions[msg.sender];
        if (amount == 0) revert NoContribution();

        contributions[msg.sender] = 0;

        // Transfer full contribution back (NO fee deduction)
        if (paymentToken == address(0)) {
            (bool success, ) = msg.sender.call{value: amount}("");
            if (!success) revert VestingFundingFailed();
        } else {
            IERC20(paymentToken).safeTransfer(msg.sender, amount);
        }

        emit Refunded(msg.sender, amount);
    }

    /**
     * @notice Update fee configuration
     * @dev V2.1 PATCH: LOCKED after presale starts
     */
    function setFeeConfig(FeeConfig calldata newConfig) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        // V2.1 PATCH: Can only update if UPCOMING + no contributions + before start
        if (status != Status.UPCOMING) revert ConfigLocked();
        if (totalRaised > 0) revert ConfigLocked();
        if (block.timestamp >= startTime) revert ConfigLocked();

        // Validate total equals sum
        if (newConfig.totalBps != 
            newConfig.treasuryBps + 
            newConfig.referralPoolBps + 
            newConfig.sbtStakingBps
        ) {
            revert InvalidFeeConfig();
        }

        feeConfig = newConfig;
        emit FeeConfigUpdated(newConfig);
    }

    /**
     * @notice Pause contributions only
     * @dev V2.1: Refunds CANNOT be paused
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Get contribution info
     */
    function getContributionInfo(address contributor) 
        external 
        view 
        returns (
            uint256 contribution,
            address referrer
        ) 
    {
        return (
            contributions[contributor],
            referrers[contributor]
        );
    }

    /**
     * @notice Get presale info
     */
    function getPresaleInfo() 
        external 
        view 
        returns (
            Status _status,
            uint256 _totalRaised,
            uint256 _softCap,
            uint256 _hardCap,
            uint256 _startTime,
            uint256 _endTime,
            uint256 _tgeTimestamp
        ) 
    {
        return (
            status,
            totalRaised,
            softCap,
            hardCap,
            startTime,
            endTime,
            tgeTimestamp
        );
    }
}

